"use client"

import styled from "styled-components"
import { Link, useNavigate, useLocation } from "react-router-dom"
import Button from "./Button"
import { clearToken, clearUser, getUser } from "../services/storage"

const Bar = styled.header`
  background: ${({ theme }) => theme.colors.white}; 
  border-bottom: 1px solid ${({ theme }) => theme.colors.gray[200]}; 
  position: sticky; 
  top: 0; 
  z-index: 100;
  box-shadow: ${({ theme }) => theme.shadow};
`

const Inner = styled.div`
  max-width: 1200px; 
  margin: 0 auto; 
  display: flex; 
  align-items: center; 
  justify-content: space-between; 
  padding: ${({ theme }) => theme.spacing(2)};
`

const Logo = styled(Link)`
  font-size: 24px;
  font-weight: 700;
  color: ${({ theme }) => theme.colors.primary};
  text-decoration: none;
`

const Nav = styled.nav`
  display: flex;
  gap: ${({ theme }) => theme.spacing(3)};
  align-items: center;
  
  @media (max-width: 768px) {
    gap: ${({ theme }) => theme.spacing(2)};
  }
`

const NavLink = styled(Link)`
  color: ${({ theme }) => theme.colors.text};
  font-weight: 500;
  padding: ${({ theme }) => theme.spacing(1)} ${({ theme }) => theme.spacing(2)};
  border-radius: ${({ theme }) => theme.radius};
  transition: all 0.2s ease;
  
  &:hover {
    background: ${({ theme }) => theme.colors.gray[100]};
    color: ${({ theme }) => theme.colors.primary};
  }
`

export default function Navbar() {
  const navigate = useNavigate()
  const location = useLocation()
  const user = getUser()

  const logout = () => {
    clearToken()
    clearUser()
    navigate("/")
  }

  return (
    <Bar>
      <Inner>
        <Logo to="/">ClearanceSystem</Logo>
        <Nav>
          {!user ? (
            <>
              <NavLink to="/">Home</NavLink>
              <NavLink to="/login">Login</NavLink>
              <NavLink to="/register">Register</NavLink>
            </>
          ) : (
            <>
              {user.role === "student" && (
                <>
                  <NavLink to="/student/start">Start Clearance</NavLink>
                  <NavLink to="/student/status">My Status</NavLink>
                  <NavLink to="/student/downloads">Downloads</NavLink>
                </>
              )}
              {user.role === "hod" && (
                <>
                  <NavLink to="/department/dashboard">Department</NavLink>
                </>
              )}
              {user.role === "admin" && (
                <>
                  <NavLink to="/admin/dashboard">Dashboard</NavLink>
                  <NavLink to="/admin/departments">Departments</NavLink>
                  <NavLink to="/admin/users">Users</NavLink>
                  <NavLink to="/admin/reports">Reports</NavLink>
                </>
              )}
              <Button size="sm" onClick={logout}>
                Logout
              </Button>
            </>
          )}
        </Nav>
      </Inner>
    </Bar>
  )
}
