import styled from "styled-components"

const Wrap = styled.div`
  display: grid; 
  grid-template-columns: repeat(auto-fill, minmax(180px, 1fr)); 
  gap: 12px;
`

const Item = styled.div`
  background: #fff; 
  border: 1px solid #eee; 
  border-radius: 12px; 
  padding: 10px 12px;
  small { color: #666; }
`

export default function ProgressTracker({ steps = [] }) {
  return (
    <Wrap>
      {steps.map((s) => (
        <Item key={s.department_code}>
          <strong>{s.department_name}</strong>
          <div>
            <small>Status:</small> <b>{s.status}</b>
          </div>
          {s.remarks && (
            <div>
              <small>Remarks:</small> {s.remarks}
            </div>
          )}
        </Item>
      ))}
    </Wrap>
  )
}
