import styled from "styled-components"

const Table = styled.table`
  width: 100%; 
  border-collapse: collapse; 
  background: #fff; 
  border-radius: 12px; 
  overflow: hidden;
  thead { background: #f3f4f6; }
  th, td { 
    padding: 12px 14px; 
    border-bottom: 1px solid #eee; 
    text-align: left; 
  }
`

export default Table
