"use client"

import { useEffect, useState } from "react"
import Card from "../../components/Card"
import Table from "../../components/Table"
import Button from "../../components/Button"
import api from "../../services/api"

export default function AdminDashboard() {
  const [rows, setRows] = useState([])
  const [awaitingFinal, setAwaitingFinal] = useState([])
  const [loading, setLoading] = useState(false)
  const [message, setMessage] = useState("")

  useEffect(() => {
    loadData()
  }, [])

  const loadData = async () => {
    try {
      const { data } = await api.get("/admin/overview")
      setRows(data)

      const awaitingFinalApproval = data.filter((item) => item.status === "awaiting_final")
      setAwaitingFinal(awaitingFinalApproval)
    } catch (error) {
      console.error("Error loading overview:", error)
    }
  }

  const fixClearances = async () => {
    setLoading(true)
    setMessage("")
    try {
      const { data } = await api.post("/admin/fix-clearances")
      setMessage(`‚úÖ ${data.message}`)
      // Reload overview
      await loadData()
    } catch (error) {
      setMessage(`‚ùå Error: ${error.response?.data?.error || error.message}`)
    } finally {
      setLoading(false)
    }
  }

  const approveFinal = async (clearanceId, studentName) => {
    if (!window.confirm(`Are you sure you want to give final approval for ${studentName}?`)) {
      return
    }

    try {
      await api.post(`/clearance/approve-final/${clearanceId}`)
      setMessage(`‚úÖ Final approval granted for ${studentName}`)
      await loadData() // Reload data
    } catch (error) {
      setMessage(`‚ùå Error: ${error.response?.data?.error || error.message}`)
    }
  }

  return (
    <div>
      {awaitingFinal.length > 0 && (
        <Card style={{ marginBottom: 20 }}>
          <h2>üéì Final Approvals Required ({awaitingFinal.length})</h2>
          <p style={{ color: "#666", marginBottom: 15 }}>
            These students have been cleared by all departments and are awaiting your final approval.
          </p>

          <Table>
            <thead>
              <tr>
                <th>Student</th>
                <th>Admission</th>
                <th>Progress</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {awaitingFinal.map((r) => (
                <tr key={r.id} style={{ backgroundColor: "#f8f9fa" }}>
                  <td>
                    <strong>{r.full_name}</strong>
                  </td>
                  <td>{r.admission_number}</td>
                  <td>
                    <span style={{ color: "green", fontWeight: "bold" }}>
                      ‚úÖ {r.cleared_count}/{r.total_departments} (All Cleared)
                    </span>
                  </td>
                  <td>
                    <Button
                      onClick={() => approveFinal(r.id, r.full_name)}
                      style={{
                        backgroundColor: "#28a745",
                        color: "white",
                        padding: "8px 16px",
                        fontSize: "14px",
                      }}
                    >
                      üéì Grant Final Approval
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card>
      )}

      <Card>
        <h2>Admin Dashboard - All Clearances</h2>

        <div style={{ marginBottom: 20 }}>
          <Button onClick={fixClearances} disabled={loading}>
            {loading ? "Fixing..." : "Fix Missing Department Clearances"}
          </Button>
          {message && (
            <div
              style={{
                marginTop: 10,
                padding: 10,
                background: message.includes("‚úÖ") ? "#d4edda" : "#f8d7da",
                borderRadius: 5,
                color: message.includes("‚úÖ") ? "#155724" : "#721c24",
              }}
            >
              {message}
            </div>
          )}
        </div>

        <Table>
          <thead>
            <tr>
              <th>Student</th>
              <th>Admission</th>
              <th>Status</th>
              <th>Progress</th>
            </tr>
          </thead>
          <tbody>
            {rows.map((r) => (
              <tr key={r.id}>
                <td>{r.full_name}</td>
                <td>{r.admission_number}</td>
                <td>
                  <span
                    style={{
                      padding: "4px 8px",
                      borderRadius: "4px",
                      fontSize: "12px",
                      fontWeight: "bold",
                      backgroundColor:
                        r.status === "completed"
                          ? "#d4edda"
                          : r.status === "awaiting_final"
                            ? "#fff3cd"
                            : r.status === "rejected"
                              ? "#f8d7da"
                              : "#e2e3e5",
                      color:
                        r.status === "completed"
                          ? "#155724"
                          : r.status === "awaiting_final"
                            ? "#856404"
                            : r.status === "rejected"
                              ? "#721c24"
                              : "#495057",
                    }}
                  >
                    {r.status === "awaiting_final"
                      ? "‚è≥ AWAITING FINAL"
                      : r.status === "completed"
                        ? "‚úÖ COMPLETED"
                        : r.status === "rejected"
                          ? "‚ùå REJECTED"
                          : "üîÑ IN PROGRESS"}
                  </span>
                </td>
                <td>
                  {r.cleared_count}/{r.total_departments}
                  {r.status === "awaiting_final" && (
                    <span style={{ color: "green", marginLeft: 8 }}>(Ready for Final Approval)</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Card>
    </div>
  )
}
