"use client"

import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { useAuth } from "../context/AuthContext"
import api from "../services/api"

function StudentDashboard() {
  const { user, logout } = useAuth()
  const [clearanceData, setClearanceData] = useState(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    fetchClearanceStatus()
  }, [])

  const fetchClearanceStatus = async () => {
    try {
      const response = await api.get("/clearance/status")
      setClearanceData(response.data)
    } catch (error) {
      console.error("Error fetching clearance status:", error)
    } finally {
      setLoading(false)
    }
  }

  const getStatusColor = (status) => {
    switch (status) {
      case "cleared":
        return "#4CAF50"
      case "rejected":
        return "#f44336"
      case "pending":
        return "#ff9800"
      default:
        return "#9e9e9e"
    }
  }

  const getOverallProgress = () => {
    if (!clearanceData?.departments) return 0
    const cleared = clearanceData.departments.filter((dept) => dept.status === "cleared").length
    return Math.round((cleared / clearanceData.departments.length) * 100)
  }

  if (loading) {
    return <div className="loading">Loading your clearance status...</div>
  }

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <div className="header-content">
          <h1>üéì Student Dashboard</h1>
          <div className="user-info">
            <span>Welcome, {user.full_name}</span>
            <button onClick={logout} className="logout-btn">
              Logout
            </button>
          </div>
        </div>
      </header>

      <main className="dashboard-main">
        {!clearanceData?.clearance ? (
          <div className="no-clearance">
            <div className="clearance-card">
              <h2>üìù Start Your Clearance Process</h2>
              <p>You haven't submitted a clearance request yet.</p>
              <Link to="/student/clearance-form" className="start-clearance-btn">
                Start Clearance Request
              </Link>
            </div>
          </div>
        ) : (
          <div className="clearance-status">
            <div className="status-overview">
              <div className="overview-card">
                <h2>üìä Clearance Progress</h2>
                <div className="progress-circle">
                  <div className="progress-text">
                    <span className="percentage">{getOverallProgress()}%</span>
                    <span className="label">Complete</span>
                  </div>
                </div>
                <div className="status-info">
                  <p>
                    <strong>Status:</strong> {clearanceData.clearance.status.replace("_", " ").toUpperCase()}
                  </p>
                  <p>
                    <strong>Submitted:</strong> {new Date(clearanceData.clearance.submitted_at).toLocaleDateString()}
                  </p>
                  <p>
                    <strong>Reason:</strong> {clearanceData.clearance.reason.replace("_", " ").toUpperCase()}
                  </p>
                </div>
              </div>
            </div>

            <div className="departments-grid">
              <h3>üè¢ Department Clearances</h3>
              <div className="departments-list">
                {clearanceData.departments.map((dept) => (
                  <div key={dept.id} className="department-card">
                    <div className="dept-header">
                      <h4>{dept.department_name}</h4>
                      <span className="status-badge" style={{ backgroundColor: getStatusColor(dept.status) }}>
                        {dept.status.toUpperCase()}
                      </span>
                    </div>
                    {dept.remarks && (
                      <p className="dept-remarks">
                        <strong>Remarks:</strong> {dept.remarks}
                      </p>
                    )}
                    {dept.has_dues && (
                      <p className="dept-dues">
                        <strong>Outstanding Dues:</strong> KSh {dept.dues_amount}
                      </p>
                    )}
                    {dept.cleared_at && (
                      <p className="dept-date">
                        <strong>Cleared on:</strong> {new Date(dept.cleared_at).toLocaleDateString()}
                        {dept.cleared_by_name && <span> by {dept.cleared_by_name}</span>}
                      </p>
                    )}
                  </div>
                ))}
              </div>
            </div>

            {clearanceData.approvals && clearanceData.approvals.length > 0 && (
              <div className="final-approvals">
                <h3>‚úÖ Final Approvals</h3>
                <div className="approvals-list">
                  {clearanceData.approvals.map((approval) => (
                    <div key={approval.id} className="approval-card">
                      <div className="approval-header">
                        <h4>{approval.approval_level.replace("_", " ").toUpperCase()}</h4>
                        <span
                          className="status-badge"
                          style={{ backgroundColor: approval.status === "approved" ? "#4CAF50" : "#f44336" }}
                        >
                          {approval.status.toUpperCase()}
                        </span>
                      </div>
                      <p>
                        <strong>Approved by:</strong> {approval.approved_by_name}
                      </p>
                      <p>
                        <strong>Date:</strong> {new Date(approval.approved_at).toLocaleDateString()}
                      </p>
                      {approval.remarks && (
                        <p>
                          <strong>Remarks:</strong> {approval.remarks}
                        </p>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {clearanceData.clearance.status === "completed" && (
              <div className="completion-actions">
                <div className="completion-card">
                  <h3>üéâ Clearance Completed!</h3>
                  <p>Congratulations! Your clearance has been completed successfully.</p>
                  <button className="download-btn">üìÑ Download Clearance Certificate</button>
                </div>
              </div>
            )}
          </div>
        )}
      </main>
    </div>
  )
}

export default StudentDashboard
