import styled from "styled-components"
import { Link } from "react-router-dom"
import Card from "../components/Card"
import Button from "../components/Button"
import { getUser } from "../services/storage"

const Hero = styled.section`
  text-align: center;
  padding: ${({ theme }) => theme.spacing(8)} 0;
  background: linear-gradient(135deg, ${({ theme }) => theme.colors.primary}10, ${({ theme }) => theme.colors.info}10);
  border-radius: ${({ theme }) => theme.radius};
  margin-bottom: ${({ theme }) => theme.spacing(4)};
`

const Title = styled.h1`
  font-size: 3rem;
  font-weight: 700;
  color: ${({ theme }) => theme.colors.text};
  margin-bottom: ${({ theme }) => theme.spacing(2)};
  
  @media (max-width: 768px) {
    font-size: 2rem;
  }
`

const Subtitle = styled.p`
  font-size: 1.25rem;
  color: ${({ theme }) => theme.colors.gray[600]};
  margin-bottom: ${({ theme }) => theme.spacing(4)};
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`

const Features = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: ${({ theme }) => theme.spacing(3)};
  margin-top: ${({ theme }) => theme.spacing(4)};
`

const FeatureCard = styled(Card)`
  text-align: center;
  
  h3 {
    color: ${({ theme }) => theme.colors.primary};
    margin-bottom: ${({ theme }) => theme.spacing(2)};
  }
  
  p {
    color: ${({ theme }) => theme.colors.gray[600]};
    line-height: 1.6;
  }
`

const ButtonGroup = styled.div`
  display: flex;
  gap: ${({ theme }) => theme.spacing(2)};
  justify-content: center;
  flex-wrap: wrap;
`

export default function Home() {
  const user = getUser()

  return (
    <div>
      <Hero>
        <Title>School Clearance System</Title>
        <Subtitle>
          Streamline your school clearance process. Save time, reduce paperwork, and track progress in real-time.
        </Subtitle>
        <ButtonGroup>
          {!user ? (
            <>
              <Link to="/register">
                <Button size="lg">Get Started</Button>
              </Link>
              <Link to="/login">
                <Button variant="secondary" size="lg">
                  Login
                </Button>
              </Link>
            </>
          ) : (
            <>
              {user.role === "student" && (
                <Link to="/student/start">
                  <Button size="lg">Start Clearance</Button>
                </Link>
              )}
              {user.role === "hod" && (
                <Link to="/department/dashboard">
                  <Button size="lg">Department Dashboard</Button>
                </Link>
              )}
              {user.role === "admin" && (
                <Link to="/admin/dashboard">
                  <Button size="lg">Admin Dashboard</Button>
                </Link>
              )}
            </>
          )}
        </ButtonGroup>
      </Hero>

      <Features>
        <FeatureCard>
          <h3>üéì For Students</h3>
          <p>
            Submit your clearance request online and track progress through each department. No more running around
            campus!
          </p>
        </FeatureCard>

        <FeatureCard>
          <h3>üè¢ For Departments</h3>
          <p>
            Review student clearances efficiently. Manage dues, add remarks, and approve or reject requests with ease.
          </p>
        </FeatureCard>

        <FeatureCard>
          <h3>‚öôÔ∏è For Administrators</h3>
          <p>
            Complete oversight of the clearance process. Manage departments, users, and generate comprehensive reports.
          </p>
        </FeatureCard>
      </Features>
    </div>
  )
}
