import styled from "styled-components"

const map = {
  pending: "warning",
  cleared: "success",
  rejected: "danger",
  in_progress: "info",
  completed: "success",
}

const Badge = styled.span`
  display: inline-block;
  font-size: 12px;
  padding: 4px 10px;
  border-radius: 999px;
  background: ${({ theme, status }) => theme.colors[map[status] || "secondary"]};
  color: #fff;
`

export default Badge
