"use client"

import { useEffect, useState } from "react"
import Card from "../../components/Card"
import Table from "../../components/Table"
import Field from "../../components/FormField"
import Button from "../../components/Button"
import api from "../../services/api"

export default function ManageDepartments() {
  const [rows, setRows] = useState([])
  const [form, setForm] = useState({ name: "", code: "", is_active: true })
  const [editingId, setEditingId] = useState(null)
  const [message, setMessage] = useState("")

  useEffect(() => {
    loadDepartments()
  }, [])

  const loadDepartments = async () => {
    try {
      const { data } = await api.get("/admin/departments")
      setRows(data)
    } catch (error) {
      setMessage("Error loading departments")
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      if (editingId) {
        await api.put(`/admin/departments/${editingId}`, form)
        setMessage("Department updated successfully")
        setEditingId(null)
      } else {
        await api.post("/admin/departments", form)
        setMessage("Department added successfully")
      }
      setForm({ name: "", code: "", is_active: true })
      loadDepartments()
    } catch (error) {
      setMessage("Error saving department: " + (error.response?.data?.error || error.message))
    }
  }

  const handleEdit = (dept) => {
    setForm({ name: dept.name, code: dept.code, is_active: Boolean(dept.is_active) })
    setEditingId(dept.id)
  }

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this department?")) return
    try {
      await api.delete(`/admin/departments/${id}`)
      setMessage("Department deleted successfully")
      loadDepartments()
    } catch (error) {
      setMessage("Error deleting department: " + (error.response?.data?.error || error.message))
    }
  }

  const cancelEdit = () => {
    setEditingId(null)
    setForm({ name: "", code: "", is_active: true })
  }

  return (
    <Card>
      <h2>Manage Departments</h2>

      {message && (
        <div style={{ padding: 10, background: "#f0f0f0", borderRadius: 5, marginBottom: 15 }}>{message}</div>
      )}

      <form onSubmit={handleSubmit}>
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr auto auto", gap: 12, alignItems: "end" }}>
          <Field>
            <label>Department Name</label>
            <input value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} required />
          </Field>
          <Field>
            <label>Department Code</label>
            <input value={form.code} onChange={(e) => setForm({ ...form, code: e.target.value })} required />
          </Field>
          <Field>
            <label>
              <input
                type="checkbox"
                checked={form.is_active}
                onChange={(e) => setForm({ ...form, is_active: e.target.checked })}
              />
              Active
            </label>
          </Field>
          <div style={{ display: "flex", gap: 8 }}>
            <Button type="submit">{editingId ? "Update" : "Add"}</Button>
            {editingId && (
              <Button type="button" variant="secondary" onClick={cancelEdit}>
                Cancel
              </Button>
            )}
          </div>
        </div>
      </form>

      <Table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Code</th>
            <th>Active</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r) => (
            <tr key={r.id}>
              <td>{r.name}</td>
              <td>{r.code}</td>
              <td>{r.is_active ? "Yes" : "No"}</td>
              <td>
                <div style={{ display: "flex", gap: 8 }}>
                  <Button variant="info" onClick={() => handleEdit(r)}>
                    Edit
                  </Button>
                  <Button variant="danger" onClick={() => handleDelete(r.id)}>
                    Delete
                  </Button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Card>
  )
}
