"use client"

import { useEffect, useState } from "react"
import Card from "../../components/Card"
import Table from "../../components/Table"
import Field from "../../components/FormField"
import Button from "../../components/Button"
import api from "../../services/api"

export default function ManageUsers() {
  const [rows, setRows] = useState([])
  const [form, setForm] = useState({
    full_name: "",
    email: "",
    username: "",
    role: "hod",
    department: "",
    password: "",
  })
  const [editingId, setEditingId] = useState(null)
  const [message, setMessage] = useState("")

  useEffect(() => {
    loadUsers()
  }, [])

  const loadUsers = async () => {
    try {
      const { data } = await api.get("/admin/users")
      setRows(data)
    } catch (error) {
      setMessage("Error loading users")
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      if (editingId) {
        await api.put(`/admin/users/${editingId}`, form)
        setMessage("User updated successfully")
        setEditingId(null)
      } else {
        await api.post("/admin/users", form)
        setMessage("User added successfully")
      }
      setForm({ full_name: "", email: "", username: "", role: "hod", department: "", password: "" })
      loadUsers()
    } catch (error) {
      setMessage("Error saving user: " + (error.response?.data?.error || error.message))
    }
  }

  const handleEdit = (user) => {
    setForm({
      full_name: user.full_name,
      email: user.email,
      username: user.username,
      role: user.role,
      department: user.department || "",
      password: "", // Don't populate password for security
    })
    setEditingId(user.id)
  }

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return
    try {
      await api.delete(`/admin/users/${id}`)
      setMessage("User deleted successfully")
      loadUsers()
    } catch (error) {
      setMessage("Error deleting user: " + (error.response?.data?.error || error.message))
    }
  }

  const cancelEdit = () => {
    setEditingId(null)
    setForm({ full_name: "", email: "", username: "", role: "hod", department: "", password: "" })
  }

  return (
    <Card>
      <h2>Manage Users</h2>

      {message && (
        <div style={{ padding: 10, background: "#f0f0f0", borderRadius: 5, marginBottom: 15 }}>{message}</div>
      )}

      <form onSubmit={handleSubmit}>
        <div style={{ display: "grid", gridTemplateColumns: "repeat(3,1fr)", gap: 12 }}>
          <Field>
            <label>Full Name</label>
            <input
              name="full_name"
              value={form.full_name}
              onChange={(e) => setForm({ ...form, [e.target.name]: e.target.value })}
              required
            />
          </Field>
          <Field>
            <label>Email</label>
            <input
              name="email"
              type="email"
              value={form.email}
              onChange={(e) => setForm({ ...form, [e.target.name]: e.target.value })}
              required
            />
          </Field>
          <Field>
            <label>Username</label>
            <input
              name="username"
              value={form.username}
              onChange={(e) => setForm({ ...form, [e.target.name]: e.target.value })}
              required
            />
          </Field>
          <Field>
            <label>Password {editingId && "(leave blank to keep current)"}</label>
            <input
              name="password"
              type="password"
              value={form.password}
              onChange={(e) => setForm({ ...form, [e.target.name]: e.target.value })}
              required={!editingId}
            />
          </Field>
          <Field>
            <label>Role</label>
            <select
              name="role"
              value={form.role}
              onChange={(e) => setForm({ ...form, [e.target.name]: e.target.value })}
            >
              <option value="hod">HOD</option>
              <option value="admin">Admin</option>
              <option value="student">Student</option>
            </select>
          </Field>
          <Field>
            <label>Department (if HOD)</label>
            <input
              name="department"
              value={form.department}
              onChange={(e) => setForm({ ...form, [e.target.name]: e.target.value })}
              placeholder="e.g., LIBRARY, ACCOUNTS"
            />
          </Field>
        </div>
        <div style={{ display: "flex", gap: 8, marginTop: 15 }}>
          <Button type="submit">{editingId ? "Update User" : "Create User"}</Button>
          {editingId && (
            <Button type="button" variant="secondary" onClick={cancelEdit}>
              Cancel
            </Button>
          )}
        </div>
      </form>

      <Table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Username</th>
            <th>Role</th>
            <th>Department</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r) => (
            <tr key={r.id}>
              <td>{r.full_name}</td>
              <td>{r.email}</td>
              <td>{r.username}</td>
              <td>{r.role}</td>
              <td>{r.department || "-"}</td>
              <td>
                <div style={{ display: "flex", gap: 8 }}>
                  <Button variant="info" onClick={() => handleEdit(r)}>
                    Edit
                  </Button>
                  <Button variant="danger" onClick={() => handleDelete(r.id)}>
                    Delete
                  </Button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Card>
  )
}
