import { Link } from "react-router-dom"
import styled from "styled-components"
import Card from "../../components/Card"
import Button from "../../components/Button"

const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
`

const Title = styled.h2`
  color: ${({ theme }) => theme.colors.text};
  margin-bottom: ${({ theme }) => theme.spacing(3)};
`

const Description = styled.p`
  color: ${({ theme }) => theme.colors.gray[600]};
  line-height: 1.6;
  margin-bottom: ${({ theme }) => theme.spacing(4)};
`

const StepsList = styled.ol`
  margin: ${({ theme }) => theme.spacing(3)} 0;
  padding-left: ${({ theme }) => theme.spacing(3)};
  
  li {
    margin-bottom: ${({ theme }) => theme.spacing(1)};
    color: ${({ theme }) => theme.colors.gray[700]};
  }
`

export default function StartClearance() {
  return (
    <Container>
      <Card>
        <Title>Start Your Clearance Process</Title>
        <Description>
          Welcome to the digital clearance system! This process will help you get cleared from all departments
          efficiently without the need to physically visit each office.
        </Description>

        <h3>How it works:</h3>
        <StepsList>
          <li>Fill out the clearance request form with your details</li>
          <li>Your request will be sent to all relevant departments</li>
          <li>Each department will review and approve/reject your clearance</li>
          <li>Track your progress in real-time on the status page</li>
          <li>Once all departments approve, download your clearance certificate</li>
        </StepsList>

        <Description>
          <strong>Note:</strong> Make sure all your information is accurate before submitting. You may need to settle
          any outstanding dues with departments before getting cleared.
        </Description>

        <Link to="/student/form">
          <Button size="lg">Start Clearance Request</Button>
        </Link>
      </Card>
    </Container>
  )
}
