"use client"

import { useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import Field from "../../components/FormField"
import Button from "../../components/Button"
import Card from "../../components/Card"
import { registerStudent } from "../../services/auth"
import { saveToken, saveUser } from "../../services/storage"
import styled from "styled-components"

const Container = styled.div`
  max-width: 600px;
  margin: ${({ theme }) => theme.spacing(4)} auto;
`

const Title = styled.h2`
  text-align: center;
  margin-bottom: ${({ theme }) => theme.spacing(3)};
  color: ${({ theme }) => theme.colors.text};
`

const FormGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: ${({ theme }) => theme.spacing(2)};
`

const ErrorMessage = styled.div`
  color: ${({ theme }) => theme.colors.danger};
  background: ${({ theme }) => theme.colors.danger}10;
  padding: ${({ theme }) => theme.spacing(1.5)};
  border-radius: ${({ theme }) => theme.radius};
  margin-bottom: ${({ theme }) => theme.spacing(2)};
  border: 1px solid ${({ theme }) => theme.colors.danger}30;
`

const LinkText = styled.p`
  text-align: center;
  margin-top: ${({ theme }) => theme.spacing(3)};
  color: ${({ theme }) => theme.colors.gray[600]};
  
  a {
    color: ${({ theme }) => theme.colors.primary};
    font-weight: 600;
  }
`

export default function RegisterStudent() {
  const [form, setForm] = useState({
    full_name: "",
    admission_number: "",
    kcse_year: "",
    class_form: "",
    stream: "",
    phone: "",
    email: "",
    password: "",
  })
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)
  const navigate = useNavigate()

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value })

  const onSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    try {
      const { token, user } = await registerStudent(form)
      saveToken(token)
      saveUser(user)
      navigate("/student/start")
    } catch (err) {
      setError(err?.response?.data?.message || "Registration failed")
    } finally {
      setLoading(false)
    }
  }

  return (
    <Container>
      <Card>
        <Title>Student Registration</Title>
        <form onSubmit={onSubmit}>
          <FormGrid>
            <Field>
              <label>Full Name *</label>
              <input name="full_name" value={form.full_name} onChange={onChange} placeholder="John Doe" required />
            </Field>
            <Field>
              <label>Admission Number *</label>
              <input
                name="admission_number"
                value={form.admission_number}
                onChange={onChange}
                placeholder="ADM001"
                required
              />
            </Field>
            <Field>
              <label>KCSE Year</label>
              <input name="kcse_year" type="number" value={form.kcse_year} onChange={onChange} placeholder="2024" />
            </Field>
            <Field>
              <label>Class/Form</label>
              <input name="class_form" value={form.class_form} onChange={onChange} placeholder="Form 4" />
            </Field>
            <Field>
              <label>Stream</label>
              <input name="stream" value={form.stream} onChange={onChange} placeholder="A" />
            </Field>
            <Field>
              <label>Phone Number</label>
              <input name="phone" value={form.phone} onChange={onChange} placeholder="+254700000000" />
            </Field>
          </FormGrid>

          <Field>
            <label>Email Address *</label>
            <input
              name="email"
              type="email"
              value={form.email}
              onChange={onChange}
              placeholder="john@example.com"
              required
            />
          </Field>
          <Field>
            <label>Password *</label>
            <input
              name="password"
              type="password"
              value={form.password}
              onChange={onChange}
              placeholder="••••••••"
              required
            />
          </Field>

          {error && <ErrorMessage>{error}</ErrorMessage>}
          <Button type="submit" disabled={loading} style={{ width: "100%" }}>
            {loading ? "Creating Account..." : "Create Account"}
          </Button>
        </form>
        <LinkText>
          Already have an account? <Link to="/login">Sign In</Link>
        </LinkText>
      </Card>
    </Container>
  )
}
