"use client"

import { useEffect, useState } from "react"
import Card from "../../components/Card"
import ProgressTracker from "../../components/ProgressTracker"
import StatusBadge from "../../components/StatusBadge"
import Button from "../../components/Button"
import api from "../../services/api"

export default function ClearanceStatus() {
  const [clearance, setClearance] = useState(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    ;(async () => {
      try {
        const { data } = await api.get("/clearance/my-latest")
        setClearance(data)
      } catch (error) {
        console.error("Error loading clearance:", error)
      } finally {
        setLoading(false)
      }
    })()
  }, [])

  const downloadCertificate = async () => {
    try {
      const response = await api.get(`/clearance/${clearance.id}/certificate`, {
        responseType: "blob",
      })

      const blob = new Blob([response.data], { type: "application/pdf" })
      const url = window.URL.createObjectURL(blob)
      const link = document.createElement("a")
      link.href = url
      link.download = `clearance_${clearance.admission_number}.pdf`
      document.body.appendChild(link)
      link.click()
      document.body.removeChild(link)
      window.URL.revokeObjectURL(url)
    } catch (error) {
      console.error("Error downloading certificate:", error)
      alert("Error downloading certificate. Please try again.")
    }
  }

  if (loading) return <div>Loading...</div>
  if (!clearance || !clearance.id)
    return (
      <Card>
        <p>No clearance request found.</p>
      </Card>
    )

  const clearedCount = clearance.departments?.filter((d) => d.status === "cleared").length || 0
  const totalCount = clearance.departments?.length || 0
  const rejectedCount = clearance.departments?.filter((d) => d.status === "rejected").length || 0

  return (
    <Card>
      <h2>Clearance Status</h2>

      <div style={{ marginBottom: 20 }}>
        <p>
          <strong>Admission Number:</strong> {clearance.admission_number}
        </p>
        <p>
          <strong>Status:</strong> <StatusBadge status={clearance.status} />
        </p>
        <p>
          <strong>Submitted:</strong> {new Date(clearance.submitted_at).toLocaleDateString()}
        </p>

        {clearance.status === "awaiting_final" && (
          <div
            style={{
              padding: 15,
              backgroundColor: "#fff3cd",
              border: "1px solid #ffeaa7",
              borderRadius: 5,
              marginTop: 10,
            }}
          >
            <h4 style={{ color: "#856404", margin: 0 }}>ðŸŽ“ Awaiting Final Approval</h4>
            <p style={{ color: "#856404", margin: "5px 0 0 0" }}>
              Congratulations! All departments have cleared you. Your clearance is now awaiting final approval from the
              Principal.
            </p>
          </div>
        )}

        {clearance.status === "completed" && (
          <div
            style={{
              padding: 15,
              backgroundColor: "#d4edda",
              border: "1px solid #c3e6cb",
              borderRadius: 5,
              marginTop: 10,
            }}
          >
            <h4 style={{ color: "#155724", margin: 0 }}>âœ… Clearance Completed!</h4>
            <p style={{ color: "#155724", margin: "5px 0 0 0" }}>
              Your clearance has been fully approved. You can now download your clearance certificate.
            </p>
            <Button
              onClick={downloadCertificate}
              style={{
                marginTop: 10,
                backgroundColor: "#28a745",
                color: "white",
              }}
            >
              ðŸ“„ Download Certificate
            </Button>
          </div>
        )}
      </div>

      <ProgressTracker cleared={clearedCount} total={totalCount} rejected={rejectedCount} />

      {clearance.departments && (
        <div style={{ marginTop: 20 }}>
          <h3>Department Status</h3>
          {clearance.departments.map((dept) => (
            <div
              key={dept.id}
              style={{
                padding: 10,
                margin: "5px 0",
                border: "1px solid #ddd",
                borderRadius: 5,
                backgroundColor:
                  dept.status === "cleared" ? "#f8f9fa" : dept.status === "rejected" ? "#fff5f5" : "#fffbf0",
              }}
            >
              <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <strong>{dept.department_name}</strong>
                <StatusBadge status={dept.status} />
              </div>
              {dept.remarks && (
                <p style={{ margin: "5px 0 0 0", fontSize: "14px", color: "#666" }}>
                  <strong>Remarks:</strong> {dept.remarks}
                </p>
              )}
              {dept.has_dues && (
                <p style={{ margin: "5px 0 0 0", fontSize: "14px", color: "#d63384" }}>
                  <strong>Dues:</strong> KSh {dept.dues_amount}
                </p>
              )}
            </div>
          ))}
        </div>
      )}
    </Card>
  )
}
