"use client"

import { useEffect, useState } from "react"
import Card from "../../components/Card"
import Button from "../../components/Button"
import api from "../../services/api"

export default function Downloads() {
  const [latest, setLatest] = useState(null)

  useEffect(() => {
    ;(async () => {
      const { data } = await api.get("/clearance/my-latest")
      setLatest(data)
    })()
  }, [])

  const download = async () => {
    const res = await api.get(`/clearance/${latest.id}/certificate`, {
      responseType: "blob",
    })
    const url = window.URL.createObjectURL(new Blob([res.data]))
    const a = document.createElement("a")
    a.href = url
    a.download = `clearance_${latest.admission_number}.pdf`
    a.click()
  }

  if (!latest) return <div>Loading...</div>
  if (latest.status !== "completed") return <Card>Clearance not completed yet.</Card>

  return (
    <Card>
      <h2>Download Clearance Certificate</h2>
      <Button onClick={download}>Download PDF</Button>
    </Card>
  )
}
