"use client"

import styled from "styled-components"
import { Link, useNavigate } from "react-router-dom"
import Button from "./Button"
import { clearToken, clearUser, getUser } from "../services/storage"

const Bar = styled.header`
  background: #fff; 
  border-bottom: 1px solid #eee; 
  position: sticky; 
  top: 0; 
  z-index: 20;
`

const Inner = styled.div`
  max-width: 1100px; 
  margin: 0 auto; 
  display: flex; 
  align-items: center; 
  justify-content: space-between; 
  padding: 12px;
`

export default function Navbar() {
  const navigate = useNavigate()
  const user = getUser()

  const logout = () => {
    clearToken()
    clearUser()
    navigate("/login")
  }

  return (
    <Bar>
      <Inner>
        <Link to="/">Kenya High School Clearance System</Link>
        <nav style={{ display: "flex", gap: 12, alignItems: "center" }}>
          <Link to="/student/start">Start</Link>
          <Link to="/student/status">Status</Link>
          {user?.role === "hod" && <Link to="/department/dashboard">Dept</Link>}
          {user?.role === "admin" && <Link to="/admin/dashboard">Admin</Link>}
          {user ? <Button onClick={logout}>Logout</Button> : <Link to="/login">Login</Link>}
        </nav>
      </Inner>
    </Bar>
  )
}
