import styled from "styled-components"

const Button = styled.button`
  background: ${({ theme, variant = "primary" }) => theme.colors[variant]};
  color: ${({ theme, variant = "primary" }) => (variant === "secondary" ? theme.colors.text : theme.colors.white)};
  border: ${({ theme, variant = "primary" }) =>
    variant === "secondary" ? `1px solid ${theme.colors.gray[300]}` : "0"};
  padding: ${({ theme, size = "md" }) =>
    size === "sm"
      ? theme.spacing(1) + " " + theme.spacing(2)
      : size === "lg"
        ? theme.spacing(2) + " " + theme.spacing(4)
        : theme.spacing(1.5) + " " + theme.spacing(3)};
  border-radius: ${({ theme }) => theme.radius};
  box-shadow: ${({ theme }) => theme.shadow};
  cursor: pointer;
  font-weight: 600;
  font-size: ${({ size = "md" }) => (size === "sm" ? "14px" : size === "lg" ? "18px" : "16px")};
  transition: all 0.2s ease;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: ${({ theme }) => theme.spacing(1)};

  &:hover {
    transform: translateY(-1px);
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }

  &:disabled { 
    opacity: 0.6; 
    cursor: not-allowed;
    transform: none;
  }
`

export default Button
